import Card from "./components/Card";
import Header from "./components/Header";
import Drawer from "./components/Drawer";
import React from "react";



function App() {

  const [cartOpened, setCartOpened]= React.useState(false);
  const [items, setItems]= React.useState([]);
  const [cartItems, setCartItems]= React.useState([]);

  React.useEffect(()=>{
    fetch('https://668bd89b0b61b8d23b0b6a22.mockapi.io/items')
    .then((res)=>{
      return res.json();
    })
    .then((json)=>{
      setItems(json);
    });
  },[]);


  const onAddToCart = (obj) =>{

    if (!cartItems.find((item) => item.id === obj.id)) {
      setCartItems((prev) => [...prev, obj]);
    }
  };

  return (
    <div className="wrapper clear">
    {cartOpened ? <Drawer items = {cartItems} onCloseCart={()=> setCartOpened(false)}></Drawer> : null}


    <Header onClickCart= {()=> setCartOpened(true)}></Header>

      <div className="content p-40">
       <div className="d-flex justify-between align-center mb-40">
        <h1>Все кроссовки</h1>
        <div className="search d-flex">
          <img src="/img/search.svg" alt="Search"></img>
          <input placeholder="Поиск..."></input>
        </div>
       </div>

        <div className="sneakers d-flex flex-wrap ">
          {items.map((item) => (
            <Card title={item.title} price={item.price} imageUrl={item.imageUrl} OnPlus = {(obj) => onAddToCart(obj)}></Card>
          ))}


        </div>
      </div>
    </div>
  );
}

export default App;
