import Card from "./components/Card";
import Header from "./components/Header";
import Drawer from "./components/Drawer";
import React from "react";
import axios from "axios";


function App() {

  const [cartOpened, setCartOpened]= React.useState(false);
  const [items, setItems]= React.useState([]);
  const [searchValue, setSearchValue]= React.useState(['']);
  const [cartItems, setCartItems]= React.useState([]);
  const [favoritesItems, setFavoritesItems]= React.useState([]);

  React.useEffect(()=>{
    axios.get('https://668bd89b0b61b8d23b0b6a22.mockapi.io/items').then((res) => {
      setItems(res.data);
    });

    axios.get('https://668bd89b0b61b8d23b0b6a22.mockapi.io/cart').then((res) => {
      setCartItems(res.data);
    });

    axios.get('https://6690e7dc26c2a69f6e8d91bd.mockapi.io/favorites').then((res) => {
      setFavoritesItems(res.data);
    });
  },[]);


  const onAddToCart = (obj) =>{

    axios.post('https://668bd89b0b61b8d23b0b6a22.mockapi.io/cart', obj);
    setCartItems((prev) => [...prev, obj]);
  };

  const onChangeSearchInput = (event) =>{
    setSearchValue(event.target.value);
  };

  const onRemoveItem = (id) =>{
    axios.delete(`https://668bd89b0b61b8d23b0b6a22.mockapi.io/cart/${id}`);
    setCartItems((prev)=> prev.filter(item => item.id !== id));
  }

  const onAddToFavorites = (obj) =>{
    axios.post('https://6690e7dc26c2a69f6e8d91bd.mockapi.io/favorites', obj);
    setFavoritesItems((prev) => [...prev, obj]);
  }

  return (
    <div className="wrapper clear">
    {cartOpened ? <Drawer items = {cartItems} onCloseCart={()=> setCartOpened(false)} onRemove={onRemoveItem}></Drawer> : null}


    <Header onClickCart= {()=> setCartOpened(true)}></Header>

      <div className="content p-40">
       <div className="d-flex justify-between align-center mb-40">
        <h1>{searchValue ? `Поиск по запросу: "${searchValue}"`: 'Все кроссовки'}</h1>
        <div className="search d-flex">
          <img src="/img/search.svg" alt="Search"></img>
          <input onChange={onChangeSearchInput} value = {searchValue} placeholder="Поиск..."></input>
         {searchValue && <img  onClick={() => setSearchValue('')} className="clear cu-p" src="/img/btn-remove.svg" alt="Clear"></img>}
        </div>
       </div>

        <div className="sneakers d-flex flex-wrap ">
          {items
          .filter((item) => item.title.toLowerCase().includes(searchValue))
          .map((item, index) => (
            <Card key = {index} title={item.title} price={item.price} imageUrl={item.imageUrl} OnPlus = {(obj) => onAddToCart(obj)} OnLiked= {(obj)=> onAddToFavorites(obj)}></Card>
          ))}


        </div>
      </div>
    </div>
  );
}

export default App;
