import styles from './Card.module.scss'
import React from 'react';


function Card({title,price,imageUrl, OnPlus}){

  const [isPlused, setIsPlused] = React.useState(false);
  const [isLiked, setIsLiked] = React.useState(false);

  const onClickPlus = () => {
    setIsPlused(!isPlused);
    OnPlus({title,price,imageUrl})
  };

  const onClickLiked = () => {
      setIsLiked(!isLiked);
  }

    return(
    <div className={styles.card}>
            <div onClick= {onClickLiked} className={styles.favorite}>
              <img src={isLiked ? '/img/liked.svg': '/img/heart-unliked.svg'} alt="Unliked"></img>
            </div>
           
            <img  width={133} height={112} src={imageUrl} alt="Sneakers 1"></img>
            <h5>{title}</h5>
            <div className="d-flex justify-between align-center">
              <div className="d-flex flex-column">
                <span>Цена:</span>
                <b>{price} руб.</b>
              </div>
              <button onClick={onClickPlus} className="button">
                <img width={32} height={32} src={isPlused ? '/img/btn-checked.svg' : '/img/btn-plus.svg' } alt="Plus"></img>
              </button>
            </div>
    </div>
    );
}

export default Card;
