import Card from "./components/Card";
import Header from "./components/Header";
import Drawer from "./components/Drawer";
import React from "react";
const sneakers = [
  {
    title: 'Мужские Кроссовки Nike Blazer Mid Suede',
    price: 12999,
    imageUrl: '/img/sneaker/1.png',
  },
  {
    title: 'Мужские Кроссовки Nike Air Max 270',
    price: 12999,
    imageUrl: '/img/sneaker/2.png',
  },
  {
    title: 'Мужские Кроссовки Nike Blazer Mid Suede',
    price: 8499,
    imageUrl: '/img/sneaker/3.png',
  },
  {
    title: 'Кроссовки Puma X Aka Boku Future Rider',
    price: 8499,
    imageUrl: '/img/sneaker/4.png',
  },
];

function App() {

  const [cartOpened, setCartOpened]= React.useState(false);

  return (
    <div className="wrapper clear">
    <Drawer onCloseCart={()=> setCartOpened(false)}></Drawer>


    <Header onClickCart= {()=> setCartOpened(true)}></Header>

      <div className="content p-40">
       <div className="d-flex justify-between align-center mb-40">
        <h1>Все кроссовки</h1>
        <div className="search d-flex">
          <img src="/img/search.svg" alt="Search"></img>
          <input placeholder="Поиск..."></input>
        </div>
       </div>

        <div className="sneakers d-flex ">
          {sneakers.map((obj) => (
            <Card title={obj.title} price={obj.price} imageUrl={obj.imageUrl} ></Card>
          ))}


        </div>
      </div>
    </div>
  );
}

export default App;
