import AppContext from "../context";
import Info from "./Info";
import React from "react";

function Drawer({onCloseCart, items = [], onRemove}){
    const [isOrdered, setIsOrdered] = React.useState(false);
    const {setCartItems} = React.useContext(AppContext);

    const onClickOrder = () => {
        setIsOrdered(true);
        setCartItems([]);
    }

    return(
        <div className="overlay">
        
            <div className="drawer">
            <h2 className="mb-20 d-flex justify-between align-center">
                Корзина
            <img onClick= {onCloseCart} className="removebtn cu-p" src="/img/btn-remove.svg" alt="Remove"></img>
            </h2>

            {items.length > 0 ? (
                <div className="d-flex flex-column flex">
                    <div className="items ">
                    {items.map((obj)=>(
                        <div key={obj.id} className="cartItem d-flex align-center justify-between  mb-20">
                    <img className=" cartItemSneakers mr-20" src={obj.imageUrl} alt="Sneakers 2"></img>
                    <div className="mr-15">
                        <p className="mb-5">{obj.title}</p>
                        <b>{obj.price} руб.</b>
                    </div>
                    <img className="removebtn" onClick={()=>onRemove(obj.id)} src="/img/btn-remove.svg" alt="Remove"></img>
                    </div>
    
                    ))}
                    </div>

                    <div className="cartTotalBlock">
                <ul >
                <li>
                    <span>Итого: </span>
                    <div></div>
                    <b>21 498 руб. </b>
                </li>
                <li >
                    <span>Налог 5%: </span>
                    <div></div>
                    <b>1074 руб. </b>
                </li>
                </ul>
                <button onClick={onClickOrder} className="greenButton">Оформить заказ <img src="/img/arrow.svg" alt="Arrow"></img></button>
            </div>
            
                </div>    
                ) : (
                    <Info title={isOrdered ? "Заказ оформлен!" : "Корзина пустая"} description={isOrdered ? "Ваш заказ #18 скоро будет передан курьерской доставке" : "Добавьте хотя бы одну пару кроссовок, чтобы сделать заказ."} image ={ isOrdered ? "/img/ordered-cart.svg" : "/img/empty-cart.svg"} ></Info>
                    
                )
            }
           
           


            
            </div>
        </div>
    );
}

export default Drawer;
