import styles from './Card.module.scss'
import React from 'react';
import ContentLoader from "react-content-loader";


function Card({id, title,price,imageUrl, OnPlus, OnLiked, favorited = false, added = false}){

  const [isPlused, setIsPlused] = React.useState(added);
  const [isLiked, setIsLiked] = React.useState(favorited);

  const onClickPlus = () => {
    setIsPlused(!isPlused);
    OnPlus({id, title,price,imageUrl});
  };

  const onClickLiked = () => {
      setIsLiked(!isLiked);
      OnLiked({id, title,price,imageUrl});
  }

    return(
    <div className={styles.card}>
             <ContentLoader 
    speed={2}
    width={150}
    height={265}
    viewBox="0 0 150 255"
    backgroundColor="#f3f3f3"
    foregroundColor="#ecebeb"
    
  >
    <rect x="0" y="0" rx="10" ry="10" width="150" height="120" /> 
    <rect x="-6" y="109" rx="5" ry="5" width="150" height="15" /> 
    <rect x="0" y="130" rx="5" ry="5" width="90" height="15" /> 
    <rect x="0" y="173" rx="5" ry="5" width="80" height="24" /> 
    <rect x="117" y="173" rx="5" ry="5" width="32" height="32" />
  </ContentLoader>
    </div>
    );
}

export default Card;
