import Card from "../components/Card";

function Home ({items, searchValue, onAddToCart, onAddToFavorites,setSearchValue, onChangeSearchInput }){
    return (
        <div className="content p-40">
       <div className="d-flex justify-between align-center mb-40">
        <h1>{searchValue ? `Поиск по запросу: "${searchValue}"`: 'Все кроссовки'}</h1>
        <div className="search d-flex">
          <img src="/img/search.svg" alt="Search"></img>
          <input onChange={onChangeSearchInput} value = {searchValue} placeholder="Поиск..."></input>
         {searchValue && <img  onClick={() => setSearchValue('') } className="clear cu-p" src="/img/btn-remove.svg" alt="Clear"></img>}
        </div>
       </div>

        <div className="sneakers d-flex flex-wrap ">
          {items
          .filter((item) => item.title.toLowerCase().includes(searchValue))
          .map((item, index) => (
            <Card key = {index} title={item.title} price={item.price} imageUrl={item.imageUrl} OnPlus = {(obj) => onAddToCart(obj)} OnLiked= {(obj)=> onAddToFavorites(obj)}></Card>
          ))}


        </div>
      </div>
    );
}


export default Home;
